	/**
	 * Launch the application.
	 */
	public static void main(String[] args) {
		
		EventQueue.invokeLater(new Runnable() {
			public void run() {
				try {
					JFrame frame = new RPG_App();
					frame.addKeyListener(new KeyAdapter() {
						public void keyPressed(KeyEvent e) {
							int keyCode = e.getKeyCode();
							Keybindings.action(keyCode, background_panel, player_panel); // Put affected panels here
						}
					});
					frame.setVisible(true);
				} catch (Exception e) {
					e.printStackTrace();
				}

			}
		});
	}

	public RPG_App() {
		setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
		setBounds(100, 250, Constants.SCREEN_SIZE_X, Constants.SCREEN_SIZE_Y);
		contentPane = new JPanel();
		contentPane.setBorder(new EmptyBorder(5, 5, 5, 5));
		setContentPane(contentPane);
		contentPane.setLayout(new GridLayout(1, 0, 0, 0));

		contentPane.add(layeredPane);
		layeredPane.setLayout(null);

		// Initialize the panels with z layer values
		Background background = new Background(layeredPane, background_panel, 0, map);
		Player player = new Player(layeredPane, player_panel, 1);
		test_enemy = new Enemy1(layeredPane, enemies_panel, 1, 50, 50);
		new Keybindings(player, background);
		//moveEnemies();

	}
	
	/*
	 * Methods go here (even if unused)
	 */
	public static int getCurrentMilliseconds() {
		LocalDateTime now = LocalDateTime.now();
		DateTimeFormatter Mformatter = DateTimeFormatter.ofPattern("mm");
		DateTimeFormatter Sformatter = DateTimeFormatter.ofPattern("ss");
		DateTimeFormatter mmformatter = DateTimeFormatter.ofPattern("SSS");
		String strMin = now.format(Mformatter);
		String strSec = now.format(Sformatter);
		String strMilli = now.format(mmformatter);
		int min = Integer.valueOf(strMin);
		int sec = Integer.valueOf(strSec);
		int milli = Integer.valueOf(strMilli);
		if (min == 0)
			min += 60;
		int time = ((min * 60 * 1000) + (sec * 1000) + milli);
		return time;
	}

	public static void sleepy(int milli) {
		try {
			// to sleep 10 seconds
			Thread.sleep(milli);
		} catch (InterruptedException epenis) {
			// recommended because catching InterruptedException clears interrupt flag
			Thread.currentThread().interrupt();
			// you probably want to quit if the thread is interrupted
			return;
		}
	}
	private static void moveEnemies() {
		ScheduledExecutorService scheduler = Executors.newScheduledThreadPool(1); // Create a thread pool with 1 thread
        Timers runnable = new Timers();

        // Schedule the runnable to run every 3 seconds after an initial delay of 1 second
        scheduler.scheduleAtFixedRate(runnable, 1, Constants.ENEMY_PACE, TimeUnit.MILLISECONDS);
        // Schedule to run once after a delay of 5 seconds
        //timer.scheduleAtFixedRate(task.moveEnemy(test_enemy, enemies_panel), 0, 3000); // 0 delay, every 3 sec

	}
